function SkeletonBasic() {
  return (
    <Div>
      <Skeleton
        width={256}
        height={256}
      />
      <Skeleton
        mt={2}
        variant="line"
      />
      <Skeleton
        mt={2}
        variant="circular"
        width={256}
        height={256}
      />
    </Div>
  )
}

function SkeletonWaveAnimation() {
  return (
    <Div>
      <Skeleton
        width={256}
        height={256}
        animation="wave"
      />
      <Skeleton
        mt={2}
        variant="line"
        animation="wave"
      />
      <Skeleton
        mt={2}
        variant="circular"
        width={64}
        height={64}
        animation="wave"
      />
    </Div>
  )
}

function SkeletonNoAmination() {
  return (
    <Div>
      <Skeleton
        width={256}
        height={256}
        animation={false}
      />
      <Skeleton
        mt={2}
        variant="line"
        animation={false}
      />
      <Skeleton
        mt={2}
        variant="circular"
        width={64}
        height={64}
        animation={false}
      />
    </Div>
  )
}
