function SwitchBasic() {
  const [checked, setChecked] = useState<any>(null)

  return (
    <Switch
      checked={checked}
      onChange={event => setChecked(event.target.checked)}
    />
  )
}

function SwitchLabels() {
  const [checked, setChecked] = useState<any>(null)

  return (
    <Div>
      <Switch
        checked={checked}
        onChange={event => setChecked(event.target.checked)}
      >
        A switch
      </Switch>
      <Switch
        mt={2}
        labelPosition="left"
        checked={checked}
        onChange={event => setChecked(event.target.checked)}
      >
        A switch
      </Switch>
      <Switch
        mt={2}
        labelPosition="top"
        checked={checked}
        onChange={event => setChecked(event.target.checked)}
      >
        A switch
      </Switch>
      <Switch
        mt={2}
        labelPosition="bottom"
        checked={checked}
        onChange={event => setChecked(event.target.checked)}
      >
        A switch
      </Switch>
    </Div>
  )
}
