function AvatarDemo(args: any) {
  return (
    <>
      <Flex>
        <Avatar {...args} />
        <Avatar
          ml={0.5}
          name="Jane Smith"
          {...args}
        />
        <Avatar
          ml={0.5}
          name="Henry Doe"
          {...args}
        />
        <Avatar
          ml={0.5}
          name="Alexander Taze"
          {...args}
        />
        <Avatar
          ml={0.5}
          src="/profile-picture.jpeg"
          {...args}
        />
      </Flex>
      <ExtendTheme theme={{ Avatar: { Root: [{ borderRadius: 4 }] } }}>
        <Flex mt={0.5}>
          <Avatar {...args} />
          <Avatar
            ml={0.5}
            name="Jane Smith"
            {...args}
          />
          <Avatar
            ml={0.5}
            name="Henry Doe"
            {...args}
          />
          <Avatar
            ml={0.5}
            name="Alexander Taze"
            {...args}
          />
          <Avatar
            ml={0.5}
            src="/profile-picture.jpeg"
            {...args}
          />
        </Flex>
      </ExtendTheme>
    </>
  )
}
