function ModalBasic() {
  const [open, setOpen] = useState(false)

  return (
    <>
      <A
        onClick={() => setOpen(true)}
        userSelect="none"
      >
        Open
      </A>
      <Modal
        open={open}
        onClose={() => setOpen(false)}
      >
        Content
      </Modal>
    </>
  )
}

function ModalNoFade() {
  const [open, setOpen] = useState(false)

  return (
    <>
      <A
        onClick={() => setOpen(true)}
        userSelect="none"
      >
        Open
      </A>
      <Modal
        fade={false}
        open={open}
        onClose={() => setOpen(false)}
      >
        Content
      </Modal>
    </>
  )
}

function ModalNoOpen() {
  return (
    <>
      <A
        userSelect="none"
      >
        Open
      </A>
      <Modal
        open={false}
      >
        Content
      </Modal>
    </>
  )
}

function ModalNoRealWorld() {
  const [value, setValue] = useState(null)

  return (
    <>
      <A
        onClick={() => {
          setValue('value')
          setTimeout(() => {
            setValue(null)
          }, 1000)
        }}
        userSelect="none"
      >
        Set value
      </A>
      <P mt={1}>
        {value ?? 'null'}
      </P>
      <Modal
        open={!!value}
        onClose={() => setValue(null)}
      >
        {value}
      </Modal>
    </>
  )
}

function ModalCloseButton() {
  const [open, setOpen] = useState(false)

  return (
    <>
      <A
        onClick={() => setOpen(true)}
        userSelect="none"
      >
        Open
      </A>
      <Modal
        open={open}
        onClose={() => setOpen(false)}
      >
        <Button onClick={() => setOpen(false)}>Close</Button>
      </Modal>
    </>
  )
}

function ModalWillChangeContainer() {
  return (
    <Div
      width={256}
      height={256}
      willChange="transform"
    >
      <Modal>
        Content
      </Modal>
    </Div>
  )
}
