function ProgressBarBasic() {
  const [progress, setProgress] = useState(0.25)

  return (
    <>
      <A
        onClick={() => setProgress(x => (x + 0.25) % 1.0001)}
        userSelect="none"
      >
        Progress
      </A>
      <ProgressBar
        mt={1}
        value={progress}
      />
    </>
  )
}
